lesson = ['\ufeffLeçon seize', 'Un (une) couple heureux', '- Henriette a [de] grands yeux bleus (grands et bleus).', '- Ceux (les) de Jaime sont petits et noirs.', '- Elle est petite, forte et brune.', '- Il est grand, maigre et roux.', '- Quand [ils] vont au restaurant, elle commande toujours [de la] viande avec [des] (patates) frites', 'et il commande presque toujours [du] poisson et [des] légumes.', "- Elle aime (A elle lui plaisent) les chiens et il adore (à lui l'enchantent) les chats.", "[Ils] sont très différents, mais [ça] fait quinze ans qu'[ils] sont mariés et [ils] s'aiment beaucoup."] 
exercise1_correction = ['Ma femme commande toujours de la viande avec des frites quand nous allons au restaurant.', 'Mon frère est marié avec une femme rousse.', "C'est un couple heureux et ils s'aiment beaucoup.", 'Mon chat et mon chien sont très différents.', 'Mon père est grand et maigre.']